template_files.rocketchat - alert:
{{ define "rocketchatAlert" }}
# {{ .Labels.alertname }}
## Labels
{{ range .Labels.SortedPairs }}
- {{ .Name }}: {{ .Value }}
{{ end }}
## Annotations
{{ if gt (len .Annotations) 0 }}
  {{ range .Annotations.SortedPairs }}
- {{ .Name }}: {{ .Value }}
  {{ end }}
  {{ end }}
## Links
  {{ if gt (len .SilenceURL ) 0 }}
- [Silence alert]({{ .SilenceURL }}) 
  {{ end }}
  {{ if gt (len .DashboardURL ) 0 }}
- [Go to dashboard]({{ .DashboardURL }})
  {{ end }}
{{ end }}

template_files.rocketchat - messages:
{{ define "rocketchatMessages" }}
  {{ if gt (len .Alerts.Firing) 0 }}
    {{ range .Alerts.Firing }} {{ template "rocketchatAlert" .}} {{ end }}
  {{ end }}
  {{ if gt (len .Alerts.Resolved) 0 }}
    {{ range .Alerts.Resolved }} {{ template "rocketchatAlert" .}} {{ end }}
  {{ end }}
{{ end }}
